import React, { useState } from "react";
import "./LoginPage.css";
const LoginPage = () => {
  const [user, setUser] = useState({
    email: "",
    password: "",
  });

  const userHandler = (e) => {
    const { name, value } = e.target;
    setUser({
      ...user, //spread operator
      [name]: value,
    });
  };
  return (
    <div className="card">
      <h2>Login</h2>
      <form method="POST">
        <input
          type="email"
          name="email"
          value={user.email}
          placeholder="Email"
          onChange={userHandler}
          required
        />
        <input
          type="password"
          name="password"
          value={user.password}
          placeholder="Password"
          onChange={userHandler}
          required
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default LoginPage;
